function add( place ){
	
	if( place = 'new'){
		var loadto = 'div#guestadd';
	}
	else{
		var loadto = 'div#response_' + place ;
	}
	
	$.get( siteurl + "/ajax.php?addon=guestbook&loadadd&pl=" + place )
	 	.done(function( data ) {
			$( loadto ).html( data );
		})
		.fail(function() {
			$( loadto ).html( "Laden des Formulars nicht m√∂glich!" );
		});
		
	$( loadto ).css( "display" , "block" );
	
	if( place = 'new'){
		$( "button#guestbuttdis" ).css( "display" , "block" );
		$( "button#guestbuttadd" ).css( "display" , "none" );
	}
	
	loadsumbit();
}
function dis(){
	$( "div#guestadd" ).css( "display" , "none" );
	$( "button#guestbuttdis" ).css( "display" , "none" );
	$( "button#guestbuttadd" ).css( "display" , "block" );
}
function preview(){
	var cont = $( "textarea#cont" ).val();
	var name = $( "input#name" ).val();
	
	if( cont == '' || name == '' ){
		$( "div#prew" ).html( "Bitte geben Sie einen Namen und einen Inhalt an!" );
	}
	else{	
		$.post( siteurl + "/ajax.php?addon=guestbook", { "vorschau_name": name, "vorschau_cont": cont } )
	 		.done(function( data ) {
				$( "div#prew" ).html( data );
	  		})
			.fail(function() {
				$( "div#prew" ).html( "Fehlerhafte AJAX Abfrage" );
			});
	}
		
	$( "div#prewarea" ).css( "display" , "block" );

	return false;
}

function savesubmit(){
	localStorage.setItem( 'name' , document.getElementById( 'name' ).value );
	localStorage.setItem( 'mail' , document.getElementById( 'mail' ).value );
	localStorage.setItem( 'cont' , document.getElementById( 'cont' ).value );
	
	return true;
}

function loadsumbit(){
	localStorage.setItem( 'name' , document.getElementById('name' ).value );
	localStorage.setItem( 'mail' , document.getElementById('mail' ).value );
	localStorage.setItem( 'cont' , document.getElementById('cont' ).value );
	
	return true;
}

function delsubmit(){
	localStorage.removeItem( 'name' );
	localStorage.removeItem( 'mail' );
	localStorage.removeItem( 'cont' );
	
	 return true;
}
